graphql.query:
query warehouses {
    warehouses {
        id
        phoneNumber
        address
        city
        stateAbbreviation
        zipCode
    }
}

query warehouse($id: String!) {
    warehouse(id: $id) {
        id
        phoneNumber
        address
        city
        stateAbbreviation
        zipCode
    }
}

query products {
    products {
        id
        inventoryCost
        inventoryCount
        inventoryValue
        itemCost
        itemPrice
        manufacturer
        name
    }
}

query product($id: String!) {
    product(id : $id) {
        id
        inventoryCost
        inventoryCount
        inventoryValue
        itemCost
        itemPrice
        manufacturer
        name
    }
}

query inventory($productId : String!, $warehouseId : String!) {
    inventory(productId : $productId, warehouseId: $warehouseId) {
        warehouseId
        productId
        inventoryCost
        inventoryCount
        inventoryValue
        itemCost
        itemPrice
    }
}

query inventoryInWarehouse($warehouseId : String!) {
    inventoryInWarehouse(warehouseId: $warehouseId) {
        warehouseId
        productId
        inventoryCost
        inventoryCount
        inventoryValue
        itemCost
        itemPrice
    }
}

query inventoryForProduct($productId : String!) {
    inventoryForProduct(productId : $productId) {
        warehouseId
        productId
        inventoryCost
        inventoryCount
        inventoryValue
        itemCost
        itemPrice
    }
}
